        /**
         * Sends double request:
         *
         * <pre>
         *  GET https://tln-dev-testrunner1.7sys.eu:443/...asimov_it_https_proxy_pipeline_chunked1 HTTP/1.1
         *  Connection: Keep-Alive
         *  X-OC-Raw:
         *
         *  GET https://tln-dev-testrunner1.7sys.eu:443/...asimov_it_https_proxy_pipeline_chunked2 HTTP/1.1
         *  Connection: close
         *  X-OC-Raw: SFRUUC8xLjEgMjAw...
         * </pre>
         *
         * <code>X-OC-Raw</code> contains encoded expected response:
         *
         * <pre>
         *  HTTP/1.1 200 OK
         *  Content-Type: text/plain
         *  Connection: Keep-Alive
         *  Transfer-Encoding: chunked
         *
         *  9
         *  11 Resp
         *
         *  25
         *  This is the data in the first chunk
         *
         *  1C
         *  and this is the second one
         *
         *  3
         *  con
         *  8
         *  sequence
         *  0
         *
         *  HTTP/1.1 200 OK
         *  Content-Type: text/plain
         *  Connection: close
         *  Transfer-Encoding: chunked
         *
         *  8
         *  2 Resp
         *
         *  25
         *  This is the data in the first chunk
         *
         *  1C
         *  and this is the second one
         *
         *  3
         *  con
         *  8
         *  sequence
         *  0
         *
         * </pre>
         * Expectations: Described response should be received.
         * @throws Exception
         */
        public void testHttpsChunkedPipeLine() throws Exception {
            String uri = createTestResourceUri("asimov_it_https_proxy_pipeline_chunked1", true, 443);
            PrepareResourceUtil.prepareResource(uri, false);
            String uri2 = createTestResourceUri("asimov_it_https_proxy_pipeline_chunked2", true, 443);
            PrepareResourceUtil.prepareResource(uri2, false);

            String expected1 = "HTTP/1.1 200 OK" + CRLF +
                    "Content-Type: text/plain"+CRLF+
                    "Connection: keep-alive"+CRLF+ //Keep-Alive => keep-alive
                    "Transfer-Encoding: chunked"+CRLF+CRLF +
                    "9" +CRLF+
                    "11 Resp" +CRLF+CRLF+

                    "25" +CRLF+
                    "This is the data in the first chunk" +CRLF+CRLF+

                    "1C" +CRLF+
                    "and this is the second one" +CRLF+CRLF+

                    "3" +CRLF+
                    "con" +CRLF+
                    "8" +CRLF+
                    "sequence" +CRLF+
                    "0" +CRLF+CRLF;

            String expected2 = "HTTP/1.1 200 OK" +CRLF+
                    "Content-Type: text/plain" +CRLF+
                    "Connection: close"+CRLF+
                    "Transfer-Encoding: chunked" +CRLF+CRLF+

                    "8" +CRLF+
                    "2 Resp" +CRLF+CRLF+

                    "25" +CRLF+
                    "This is the data in the first chunk" +CRLF+CRLF+

                    "1C" +CRLF+
                    "and this is the second one" +CRLF+CRLF+

                    "3" +CRLF+
                    "con" +CRLF+
                    "8" +CRLF+
                    "sequence" +CRLF+
                    "0" + CRLF+CRLF;

            String body1 =
                    "11 Resp" +CRLF+

                            "This is the data in the first chunk" +CRLF+

                            "and this is the second one" +CRLF+

                            "con" +
                            "sequence";
            String body2 =
                    "2 Resp" +CRLF+

                            "This is the data in the first chunk" +CRLF+

                            "and this is the second one" +CRLF+

                            "con" +
                            "sequence";

            String encoded1 = URLEncoder.encode(Base64.encodeToString(expected1.getBytes(), Base64.DEFAULT));
            String encoded2 = URLEncoder.encode(Base64.encodeToString(expected2.getBytes(), Base64.DEFAULT));

            String request = "GET " + uri + " HTTP/1.1" + CRLF + "Connection: keep-alive" + CRLF + "X-OC-Raw: "+ encoded1 //Keep-Alive => keep-alive
                    + CRLF + CRLF + "GET " + uri2 + " HTTP/1.1" + CRLF
                    + "Connection: close" + CRLF
                    + "X-OC-Raw: " + encoded2 + CRLF + CRLF;

            HttpResponse response = sendHttpsRequest(request.getBytes(), TEST_RESOURCE_HOST);

            ByteArrayOutputStream stream1 = new ByteArrayOutputStream();
            ByteArrayOutputStream stream2 = new ByteArrayOutputStream();

            stream1.write(new StringBuilder(response.getRawContent().substring(0, response.getRawContent().indexOf("HTTP", 5)))
                    .toString().getBytes());
            stream2.write(new StringBuilder(response.getRawContent().substring(response.getRawContent().indexOf("HTTP", 5)))
                    .toString().getBytes());

            HttpResponse response1 = buildResponse(stream1);
            HttpResponse response2 = buildResponse(stream2);

            assertEquals(HttpStatus.SC_OK, response1.getStatusCode());
            assertEquals("keep-alive", response1.getHeaderField("Connection"));
            assertEquals("text/plain", response1.getHeaderField("Content-Type"));
            assertEquals("chunked", response1.getHeaderField("Transfer-Encoding"));
            assertEquals(body1, response1.getBody());

            assertEquals(HttpStatus.SC_OK, response2.getStatusCode());
            assertEquals("close", response2.getHeaderField("Connection"));
            assertEquals("text/plain", response2.getHeaderField("Content-Type"));
            assertEquals(body2, response2.getBody());
        }

